public:
    int countHighestScoreNodes(vector<int>& parents) {
        int n=parents.size();
        unordered_map<int,TreeNode*> hashmap;
        for(int i=0;i<n;i++){
            TreeNode* temp=new TreeNode(i);
            hashmap[i]=temp;
        }
        vector<bool> visited(n,0);
        for(int i=1;i<n;i++){
            if(visited[parents[i]]){
                hashmap[parents[i]]->right=hashmap[i];
            }
            else{
                hashmap[parents[i]]->left=hashmap[i];
                visited[parents[i]]=1;
            }
        }
        unordered_map<int,pair<int,int>> subTree_hashmap;
        unsigned long long total=count(hashmap[0],subTree_hashmap);
        unsigned long long ans=0;
