func dfs(u, p int, gr [][]int, size *[]int) int {
	sze := 1
	for _, v := range gr[u] {
		if v != p {
			sze += dfs(v, u, gr, size)
		}
	}
	(*size)[u] = sze
	return sze
}

func countHighestScoreNodes(parents []int) int {
	n := len(parents)
	gr := make([][]int, n)
	for i := 0; i < n; i++ {
		gr[i] = make([]int, 0)
	}
	for idx, parent := range parents {
		if parent != -1 {
			gr[parent] = append(gr[parent], idx)
		}
	}
	scoresMap, size, maxProduct := make(map[int]int, 0), make([]int, n), 0
	dfs(0, -1, gr, &size)
	for val := range size {
		taken := 1
		product := 1
		for _, child := range gr[val] {
			taken += size[child]
			product = product * size[child]
		}
		rem := n - taken
		if rem > 1 {
			product = product * rem
		}
		scoresMap[product]++
		maxProduct = int(math.Max(float64(product), float64(maxProduct)))
	}
	return scoresMap[maxProduct]
}
