class Solution {
public:

    int dfs(vector<vector<int>>& adj, int u, vector<bool>& vis, vector<int>& count)
    {
        vis[u] = true;

        // base case, leaf node

        if(adj[u].size() == 1 && vis[adj[u][0]])
        {
            count[u] = 1;

            return 1;
        }

        int sum = 0;

        for(auto v : adj[u])
        {
            if(vis[v] == false)
            {
                sum += dfs(adj, v, vis, count);
            }
        }

        count[u] = sum + 1;

        return sum + 1;
    }

    int countHighestScoreNodes(vector<int>& parents) {

        int n = parents.size();

        // create an adjacency list

        vector<vector<int>> adj(n);

        for(int i = 1; i < n; i++)
        {
            int u = parents[i];

            int v = i;

            adj[u].push_back(v);

            adj[v].push_back(u);
        }

        vector<bool> vis(n, false);

        vector<int> count(n, 0);

        // first of all find the no. of nodes below each node including current node

        dfs(adj, 0, vis, count);

        vector<long long> prod(n, 0);

        // find the product after removing each node

        for(int u = 0; u < n; u++)
        {
            long long product = 1;

            // sum will store the no. of nodes in adjacent subtree except parent subtree

            long long sum = 0;

            for(auto v : adj[u])
            {
                if(parents[u] != v)
                {
                    sum += count[v];

                    product *= count[v];
                }
            }

            // find remaining no. of nodes in parent subtree

            long long rem = n - sum - 1;

            if(rem)
            {
                product *= rem;
            }

            prod[u] = product;
        }

        // find maximum product

        long long maxi = *max_element(prod.begin(), prod.end());

        // now count no .of nodes giving maximum product

        int ans = 0;

        for(auto x : prod)
        {
            if(x == maxi)
            {
                ans++;
            }
        }

        return ans;
    }
};
```

