class Solution {
    class TreeNode {
        private TreeNode left, right ;
        private int subtreeNodeCount = 0 ;
        private long score = 0L ;
    }

    private int setSubtreeNodeCount(TreeNode root) {
        if (root == null) return 0 ;

        int left = setSubtreeNodeCount(root.left) ;
        int right = setSubtreeNodeCount(root.right) ;

        root.subtreeNodeCount = left + right + 1 ;
        return root.subtreeNodeCount ;
    }

    public int countHighestScoreNodes(int[] parents) {
        int n = parents.length ;
        TreeNode[] node = new TreeNode [n] ;

        // this for loop is to initialize all nodes
        for (int i = 0; i < n; i++)
            node[i] = new TreeNode() ;

        // this for loop forms the tree
        for (int i = 1; i < n; i++) {
            int parent = parents[i] ;
            if (node[parent].left == null)
                node[parent].left = node[i] ;
            else node[parent].right = node[i] ;
        }

        // sets the node count of the subtree for each node
        setSubtreeNodeCount(node[0]) ;

        long maxScore = 0 ;
        int result = 0 ;

        // sets the score of each node and stores the max score also
        for (int i = 0; i < n; i++) {
            long product = 1 ;
            int leftCount = 0, rightCount = 0 ;
            int parentCount = n - node[i].subtreeNodeCount ;

            if (node[i].left != null)
                leftCount = node[i].left.subtreeNodeCount ;
            if (node[i].right != null)
                rightCount = node[i].right.subtreeNodeCount ;

            if (leftCount > 0) product *= leftCount ;
            if (rightCount > 0) product *= rightCount ;
            if (parentCount > 0) product *= parentCount ;

            node[i].score = product ;
            maxScore = Math.max(maxScore, product) ;
        }

        // counts number of nodes having maxScore
        for (int i = 0; i < n; i++)
            if (node[i].score == maxScore)
                result++ ;

        return result ;
    }
}
