typedef long long int ll;


class Solution {
public:
    void updateAnswer(ll score, ll &maximum, int &answer)
    {
        if (score > maximum)
        {
            maximum = score;
            answer = 1;
        }
        else if (score == maximum)
            answer++;
    }

    ll findCount(int i, int n, unordered_map<int, vector<int>> &adj, ll &maximum, int &answer)
    {
        if (adj.find(i) == adj.end())
        {
            updateAnswer(n - 1, maximum, answer);
            return 1;
        }

        ll val, count = 0, score = 1;

        for (int j : adj[i])
        {
            val = findCount(j, n, adj, maximum, answer);
            score *= val;
            count += val;
        }

        if ((val = n - 1 - count) != 0)
            score *= val;

        updateAnswer(score, maximum, answer);

        return count + 1;
    }

    int countHighestScoreNodes(vector<int> &parents)
    {
        int n = parents.size();

        unordered_map<int, vector<int>> adj;

        for (int i = 1; i < n; i++)
            adj[parents[i]].push_back(i);

        ll maximum = 0;
        int count = 0;

        findCount(0, n, adj, maximum, count);

        return count;
    }
};
