class Solution {
public:
    vector<int> getAverages(vector<int>& nums, int k) {
        vector<int> ans(nums.size(), -1); 
        if(ans.size() < 2*k+1) return ans; 
        stack<int> st; long long int t = 0; 
        int s = 1, e = 2*k+1; 
        for(int i=0; i<=2*k; i++) t += nums[i];  st.push(t/(2*k+1)); 
        while(e <nums.size()){
             t -= nums[s-1]; t += nums[e]; s++; e++;
             st.push(t/(2*k+1)); 
        }

        int start = k, end = nums.size()-k-1;
        for(int i=end; i>=start ; i--) {
            ans[i] = st.top(); st.pop(); 
        }

        return ans; 
    }
};
