class Solution {
public:
    vector<int> getAverages(vector<int>& nums, int k) {
        int n=nums.size();
//         create an array to store the mid vals if present
        vector<int>ans(n,-1);
//         end bounds check 
        if(n%2==0&&n/2<=k){
            return ans;
        }else if(n%2!=0&&n/2<k){
            return ans;
        }

        int j=0,i=0,sz=2*k+1,m=k;
        long long int s=0;
//         iteration over the array 
        while(i<n&&j<n){
            s+=nums[i];
            if(i-j+1==sz){
                ans[m++]=s/sz;
                s-=nums[j++];
            }
            i++;
        }
        return ans;

    }
};
