            for(int i=0;i<n;i++){
                int a;
                a = nums[i];
                ans.emplace_back(a/(2*k+1));                
            }            
        }            
        else if(k>n/2){
            for(int i=0;i<n;i++){
                ans.emplace_back(-1);
            }
        }
        else{
            for(int i=0;i<n-k;i++){
                if(i<k){
                    ans.emplace_back(-1);
                }
                else{
                    long long a;
                    if(i-k == 0 and i+k == n-1){
                        a = pre[i-1] + suf[i+1] + nums[i];
                    }
                    else if(i-k == 0 ){
                        a = pre[i-1] + suf[i+1]-suf[i+k+1] + nums[i];
                    }
                    else if(i+k == n-1){
                        a = pre[i-1]-pre[i-k-1] + suf[i+1] + nums[i];
                    }
                    else{
                        a = (pre[i-1]-pre[i-k-1]) + (suf[i+1]-suf[i+k+1]) + nums[i];
                    }
                    ans.emplace_back(a/(2*k+1));
                }
            }
            for(int i=n-k;i<n;i++){
                ans.emplace_back(-1);
            }
        }

        return ans;
    }
};
```

