class Solution {
public:
    vector<int> getAverages(vector<int>& nums, int k) {
        vector<int> ans;
        long int sum=0;
        int l=k;
        int h=k;
        int n=nums.size();
        if(2*k+1>nums.size()){
            while(n--){
            ans.push_back(-1);
        }
            return ans;
        }
        if(k==0){
            ans.push_back(nums[0]);
            return nums;
        }
        while(l--){
            ans.push_back(-1);
        }
        for(int i=0;i<nums.size();i++){
            if(i<=(2*k)){
                sum+=nums[i];
                if(i==2*k){
                    if(k>0)
                        ans.push_back(int(sum/(2*k+1)));
                }
            }
            else{
                sum=sum-nums[i-2*k-1]+nums[i];
                ans.push_back(sum/(2*k+1));
            }
        }
        while(h--){
            ans.push_back(-1);
        }
        return ans;
    }
};
