```go
func getAverages(nums []int, k int) []int {
  res := make([]int, len(nums))
  for i := 0; i < len(nums); i++ {
    res[i] = -1
  }
  sublen := 2 * k + 1 // length of the sub-array with k-radius is 2*k+1
  var sum int
  // We're going to be using a sliding window of sublen length
  for i := 0; i < len(nums); i++ {
    sum += nums[i]
    if i < sublen-1 {
      continue
    }
    res[i-k] = sum/sublen // i-k points to the right position in the result array
    sum -= nums[i-sublen+1]
  }
  return res
}
