class Solution {
public:
    vector<int> getAverages(vector<int>& nums, int k) {
        vector<int> result(nums.size(), -1);
        long long runningSum = 0;

        if(nums.size() <= (k * 2)) return result;

        for(int i = 0; i <= (k * 2); i++){
            runningSum += nums[i];
        }

        int current = k;
        result[current] = runningSum / ((k * 2) + 1);

        for(int i = 1; i + (k * 2) < nums.size(); i++){
            runningSum = (runningSum - nums[i - 1]) + nums[i + (k * 2)];
            result[++current] = runningSum / ((k * 2) + 1);
        }

        return result;
    }
};
