class Solution {
public:
    vector<int> getAverages(vector<int>& nums, int k) 
    {
        vector<int> res(nums.size(), -1);
        int left = 0, right = 2*k;
        long sum = 0;

        if(right >= nums.size()) return res;

        for(int i=0; i<=right; i++) sum += nums[i];
        res[k] = sum/(2*k+1);

        for(int i=k+1; i<nums.size()-k; i++)
        {            
            sum -= nums[left++];
            sum += nums[++right];
            res[i] = sum/(2*k+1);
        }

        return res;
    }
};
