    vector<int> getAverages(vector<int>& nums, int k) {
        int n = nums.size();
        int window = 2 * k + 1 ;
        if(window > n) return ans;

        vector<int> ans(n,-1);

        long long prefix = 0;
        for(int i=0; i<window; i++){
            prefix += nums[i];
        }

        int j = k;
        ans[j++] = prefix/window;
        for(int i=window; i<n; i++){
            prefix -= nums[i-window];
            prefix += nums[i];
            ans[j++] = prefix/window; 
        }

        return ans;
    }
};
