class Solution {
public:
    vector<int> getAverages(vector<int>& nums, int k) {
        vector<int> avg(nums.size(),-1);
        if(k == 0)
            return nums;

        if(nums.size() <= 2*k)
        {
            return avg;
        }
        int idx = 2*k + 1;
        long long sum = 0;
        sum = accumulate(nums.begin(),nums.begin()+idx,sum);

        for(int i = k; i < nums.size()-k; i++)
        {
            avg[i] = sum/idx;
            sum = sum - nums[i-k];

            if(i+k+1 == nums.size())
                break;

            sum += nums[i+k+1];
        }

        return avg;
    }
};
