        vector<int> radAvg;// array containing the radius average
        long long int s = 0;

        // storing the prefix sum
        for(auto num: nums) {
            s += num;
            prefixSum.push_back(s);
        }


        for(int i = 0;i < nums.size();i++){
            if(i - k >= 0 && i + k < nums.size()) // checking whether i - k and i + k lies within indices of array
                radAvg.push_back((prefixSum[i + k] - (i - k - 1 < 0 ? 0 : prefixSum[i - k - 1]))/(2*k + 1));
                // radial sum will be prefix[i + k] - prefix[i - k - 1]
                // if i - k - 1 < 0 then it's 0 
                // average will be sum divided By ((i + k) - (i - k) + 1)
            else 
                radAvg.push_back(-1);// less than k elements before or after the index i so -1 is inserted

        }

        // return the resultant array
        return radAvg;
    }
};
