```
class Solution {
public:
    vector<int> getAverages(vector<int>& nums, int k) {
        int n = nums.size();

        vector<long long> ans;
        ans.push_back(nums[0]);
        for(int i= 1; i<n; i++){
          ans.push_back(1ll*nums[i]+ans[i-1]);
        }

        vector<int> v;
        int f = 2*k + 1;
        for(int i = 0; i<n; i++){
          if(i-k<0 || i+k>=n) v.push_back(-1);
          else{
            if(i-k>0)v.push_back((ans[i+k]-ans[i-k-1])/f);
            else v.push_back(ans[i+k]/f);
          }
        }
        return v;
    }
};
```

