# Brute-force Solution
class Solution:
    def getAverages(self, nums: List[int], k: int) -> List[int]:
        n = len(nums)
        if n < k:   return [-1] * n
        if k == 0:  return nums

        ans = []
        for i in range(n):
            if i < k or i + k >= n:
                ans += -1,
            else:
                sum = 0
                for j in range(i-k, i+k+1):
                    sum += nums[j]
                sum //= (2 * k + 1)
                ans += sum,
        return ans
