# Code
```
class Solution {
public:
    vector<int> getAverages(vector<int>& nums, int k) {
        int n = nums.size();
        vector<int> avgs(n, -1);

        long long sum = 0;

        // Calculate sum for the first window
        for (int i = 0; i <= 2 * k && i < n; i++) {
            sum += nums[i];
        }

        // Calculate average for the first window
        if (n >= 2 * k + 1) {
            avgs[k] = sum / (2 * k + 1);
        }

        // Slide the window and calculate averages
        for (int i = k + 1; i < n - k; i++) {
            sum -= nums[i - k - 1]; // Subtract the element going out of the window
            sum += nums[i + k];     // Add the new element coming into the window
            avgs[i] = sum / (2 * k + 1);
        }

        return avgs;
    }
};
