class Solution
{
public:
    vector<int> getAverages(vector<int> &nums, int k)
    {
        vector<int> result;

        // CASE 1 //
        if (nums.size() <= k)
        {
            for (int i = 1; i <= k && nums.size() > result.size(); i++)
            {
                result.push_back(-1);
            }
            return result;
        }


        // CASE 2 //
        for (int i = 1; i <= k && nums.size() > result.size(); i++)
        {
            result.push_back(-1);
        }

        // sliding window code
        long long int sum = 0;  // sum may cause overflow
        int wsize = (2 * k) + 1;
        int i = 0, j = 0;
        while (j < nums.size())
        {
            sum += nums[j];
            if (j - i + 1 < wsize)
            {
                j++;
            }
            else if (j - i + 1 == wsize)
            {
                result.push_back(sum / wsize);
                sum -= nums[i];
                i++;
                j++;
            }
        }

        for (int i = 1; i <= k && nums.size() > result.size(); i++)
        {
            result.push_back(-1);
        }

        return result;
    }
};
