public:
    ListNode* mergeTwoLists(ListNode* l1, ListNode* l2) {

        //base case
        if(l1 == NULL)
            return l2;
        if(l2 == NULL)
            return l1;

        //recursive case
        ListNode* res;
        if(l1 -> val <= l2 -> val){
            res = l1;
            res -> next = mergeTwoLists(l1->next,l2);
        }
        else{
            res = l2;
            res -> next = mergeTwoLists(l1,l2->next);
        }

        return res;
    }
};
