class Solution {
public:
    ListNode* mergeTwoLists(ListNode* l1, ListNode* l2) {
        ListNode dummy = ListNode(0);
        ListNode* ans = &dummy;

        while(l1 && l2){
            if(l1 -> val < l2 -> val){
