/**
 * Definition for singly-linked list.
 * public class ListNode {
 *     int val;
 *     ListNode next;
 *     ListNode() {}
 *     ListNode(int val) { this.val = val; }
 *     ListNode(int val, ListNode next) { this.val = val; this.next = next; }
 * }
 */
class Solution {
    public static ListNode merge(ListNode list1, ListNode list2,ListNode finalList){
        if(list1==null){
            while(list2!=null){
                finalList.next=list2;
                list2=list2.next;
            }
            return finalList;
        }
        else if(list2==null){
            finalList.next=list1;
            while(list1!=null){
                finalList.next=list1;
                list1=list1.next;
            }
            return finalList;
        }
        if(list1.val<=list2.val){
            finalList.next=list1;
            merge(list1.next,list2,finalList.next);
        }else{
            finalList.next=list2;
            merge(list1,list2.next,finalList.next);
        }
        return finalList;

    }
    public ListNode mergeTwoLists(ListNode list1, ListNode list2) {
        int value=0;
        if (list1 == null) {
            return list2;
        }
        if (list2 == null) {
            return list1;
        }
        if(list1.val<=list2.val){
            value = list1.val;
            list1=list1.next;
        }else{
            value = list2.val;
            list2=list2.next;
        }
        ListNode finalList=new ListNode(value);
        return merge(list1,list2,finalList);

    }
}
