        if(list1==null){
            while(list2!=null){
                finalList.next=list2;
                list2=list2.next;
            }
            return finalList;
        }
        else if(list2==null){
            finalList.next=list1;
            while(list1!=null){
                finalList.next=list1;
                list1=list1.next;
            }
            return finalList;
