public class Solution {
    public ListNode MergeTwoLists(ListNode list1, ListNode list2) {
        Boolean cond = true;
        ListNode prevList1 = new ListNode(0,null);
        ListNode tempList2;


        while(cond){
            if(list2.val < list1.val){
                tempList2 = list2;
                if(prevList1 == null){
                    prevList1 = list2;
                    prevList1.next = list1;
                }else{
                    prevList1.next = list2;
                    list2.next = list1;
                }
                if(tempList2.next == null){
                    cond = false;
                    break;
                }
                list2 = tempList2.next;

            }else{
                if(list1.next == null){
                    cond = false;
                    break;
                }

                prevList1.val = list1.val;
                prevList1.next = list1.next;
                list1 = list1.next;
            }

        }
        if(list2.next != null){
            list1.next = list2;
        }
        return list1;

    }
}

