class Solution {
public:
    vector<int> goodDaysToRobBank(vector<int>& security, int time) {
        vector<int> robDays;
        int len = security.size();

        // arrays to store number of non-increasing day before ith day, initialising with 0
        vector<int> numDaysBefore(len, 0);
        // arrays to store number of non-decreasing day after ith day
        vector<int> numDaysAfter(len, 0);

        // iterate from left to right, to find non-increasing pattern and storing number of non-inc days before each particular day
        for(int i=1; i<len; i++){
            if(security[i-1] >= security[i]){
                numDaysBefore[i] = 1 + numDaysBefore[i-1];
            }
        }

        // the above step for number of non-decreasing days before each particular day
        for(int j=len-2; j>=0; j--){
            if(security[j+1] >= security[j]){
                numDaysAfter[j] = 1 + numDaysAfter[j+1];
            }
        }

        // check if the condition 2 & 3 given in Q. are satisfy
        for(int k = time; k < (len - time); k++){
            if(numDaysBefore[k] >= time && numDaysAfter[k] >= time)
                robDays.push_back(k);
        }

        return robDays;
    }
};
