    vector<int> goodDaysToRobBank(vector<int>& a, int time) 
    {
        //as we need to check criteria for i-time to i+time so this condition is necessary
        if(2*time>a.size())return {};
        vector<int>res;
        //edge case here we do not have to check for the given condition 
        //as time is 0 :)
        if(time==0)
        {
            for(int i=0;i<a.size();i++)res.push_back(i);
            return res;
        }
        //create two maps one for storing the indices(0th based)(start,end) of non-increasing subarray
        //one for storing (end,start) of non-decreasing subarray 
        //storing in this way helps in better implementation 
        map<int,int>dec,inc;
        int start=0,end=-1;

        //store the non-increasing subarray indices
        for(int i=1;i<a.size();)
        {
            if(a[i]<=a[i-1])
            {start=i-1;
               while(i<a.size()&&a[i]<=a[i-1])
            {end=i;i++;}
            dec[start]=end;  
            }
            else
                i++;
        }
        //store the non-decreasing subarray indices
         for(int i=1;i<a.size();)
        {
            if(a[i]>=a[i-1])
            {start=i-1;
               while(i<a.size()&&a[i]>=a[i-1])
            {end=i;i++;}
            inc[end]=start;  
            }
            else
                i++;
        }
        //we start from time and end at length-time for obvious reasons :')
        for(int i=time;i<a.size()-time;i++)
        {
            //here we find the start index of a non-increasing subarray just greater than
            //required criteria and check if the previous iterator's end index is 
            //greater thanor equal to (i)th value so we get a non-increasing subarray till i
            //from front
            auto it=dec.upper_bound(i-time);

            //here we find the end index of a non-decreasing subarray eual or greater than
            //required criteria and check if this iterator's start index is lesser than or equal to
            //(i)th value so we get a non-decreasing subarray till i from back
            auto it1=inc.lower_bound(i+time);
            //cases where the criteria is not possible
            //u will get it by urself ik :)
           if(it==dec.begin())continue;
           if(it1==inc.end())continue;

            it--;//this is for decrementing the non-increasing iterator as mentioned above

            //this is the required condition
                if((*it).second>=i&&(*it1).second<=i)
                res.push_back(i);
        }
        return res;
    }
};
