class Solution {
public:
    vector<int> goodDaysToRobBank(vector<int>& st, int time) {
        int cnt = 0; // count no increasing element and decreasing element at same time 
        int i = 0; // pointing to decreasing element
        int j = time; // pointing to increasing element
        vector<int> ans;
        while(j<st.size()){ //start traversing
            if(cnt==time){ // if we found cnt == time means no of decreasing && increasing  elements at left and right position are now target times then push current index into ans and cnt-- because current element is previous for next element 
                ans.push_back(i);
                cnt--;
            }
            if(j!=st.size()-1 && st[i]>=st[i+1] && st[j]<=st[j+1]) {  // if array is  decreasing && increasing at same time at i and j then cnt++
                i++;
                j++;
                cnt++;
            } 
            else{ // if any array in either non-decreasing in left and non-increasing in right or both then start a new count = 0
                i++;
                j++;
                cnt = 0;
            }
        }
        return ans;
    }
};
