public:
    vector<int> goodDaysToRobBank(vector<int>& security, int time) {
	    int n = security.size();
        int p0 = 0;
        int p1 = 0;
        vector<int> sols;

        if (time*2>n) return sols;
        if (time==0){
            sols.resize(n);
            iota(sols.begin(), sols.end(), 0);
            return sols;
