class Solution {
    public List<Integer> goodDaysToRobBank(int[] arr, int time) {
        List<Integer> res = new ArrayList<Integer>();
        int n = arr.length;
// keep two arrays pre and post
// pre[i] and post[i] will have the start index of the streak uptill i
// streak for pre is until arr[i]<=arr[i-1] is followed
// streak for post is until arr[i]<=arr[i+1] is followed

// a day will be good day to rob the bank if
//1. pre[i-1]<=i-time i.e. streak is followed for i-time days.
//2. post[i+1]>=i+timr i.e. streak is followed for i+time days.
//3. arr[i]<=arr[i-1]&&arr[i]<=arr[i+1]


        int[]pre = new int[n];
        int[] post = new int[n];
        pre[0] = 0;

        if(time==0)
        {
            for(int i=0;i<n;i++)
                res.add(i);
            return res;
        }
        for(int i=1;i<n;i++)
        {
            if(arr[i]<=arr[i-1])
                pre[i] = pre[i-1]; // prev streak continue
            else
            {
                // new streak start
                pre[i] = i;
            }
        }
        post[n-1] = n-1;
         for(int i=n-2;i>=0;i--)
        {
            if(arr[i]<=arr[i+1])
                post[i] = post[i+1]; // prev streak continue
            else
            {
                // new streak start
                post[i] = i;
            }
        }
        for(int i=time;i<n-time;i++)
        {
            if((pre[i-1]<=i-time)&&(post[i+1]>=(i+time)&&(arr[i]<=arr[i-1])&&(arr[i]<=arr[i+1])))
                res.add(i);
        }
        return res;
    }
}
