class Solution {
    public List<Integer> goodDaysToRobBank(int[] security, int time) {
        List<Integer> res = new ArrayList();
        int n = security.length, slow = 0;
        boolean[] left = new boolean[n]; // if there is a non-increasing sliding window start from [i - time, i]
        for(int i = 0; i < n; i++) {
            if(i == 0 || security[i] > security[i-1]) {  
                slow = i;
            } 
            if(i - slow >= time) {
                left[i] = true;                
