class Solution {
    public List<Integer> goodDaysToRobBank(int[] security, int time) {
        List<Integer>  res = new ArrayList<>();
        if(security == null || security.length == 0) return res;
        int n = security.length;

        // do preparations to save checking time
        // Store how many numbers larger than current number on the left side
        int[] largerCountOnLeft = new int[n];        
        int leftCount = 0;        
        for(int i = 1; i < n; i++){
             if(security[i] <= security[i-1]){
                  leftCount++;
                  largerCountOnLeft[i] = leftCount;
             }else{
                 leftCount = 0;
             }
        }

        // Store how many numbers larger than current number on the right side
        int[] largerCountOnRight = new int[n];
        int rightCount = 0;
        for(int i = n-2; i >= 0; i--){
             if(security[i] <= security[i+1]){
                  rightCount++;
                  largerCountOnRight[i] = rightCount;
             }else{
                  rightCount = 0;
             }
        }


        // check if current number is lowest than right and left and both sides has time+ numbers larger than current number
        for(int i = time; i < n - time; i++){
            if(largerCountOnLeft[i] >= time && largerCountOnRight[i] >= time){
                res.add(i);
            }             
        }
        return res;
    }
}
