        int n = security.length;
        int[] left = new int[n];
        int[] right = new int[n];

        for(int i = 1; i < n; i++){
            if(security[i-1] >= security[i]){
                left[i] = left[i-1] + 1;
            }
        }
        for(int i = n-2; i >= 0; i--){
            if(security[i] <= security[i+1]){
                right[i] = right[i+1] + 1;
            }
        }
        List<Integer> list = new ArrayList<>();
        for(int i = 0; i < n; i++){
            if(left[i] >= time && right[i] >= time){
                list.add(i);
            }
        }
        return list;
    }
}
