class Solution {
	/*
	Store A Prefix Array With The Following Condition
	security[i - time] >= security[i - time + 1] >= ...
	>= security[i]
	<= ... <= security[i + time - 1] <= security[i + time]
	 */
	public static List<Integer> goodDaysToRobBank(int[] security, int time) {
		int n = security.length;
		List<Integer> ans = new ArrayList<>();
		int[] prefLeft = new int[n];
		int[] prefRight = new int[n];

		// Count the length of non-increasing elements to the left
		for (int i = 1; i < n; i++) {
			if (security[i] <= security[i - 1])
				prefLeft[i] = prefLeft[i - 1] + 1;
		}
		// Count the length of non-decreasing elements to the right
		for (int i = n - 2; i >= 0; --i) {
			if (security[i] <= security[i + 1])
				prefRight[i] = prefRight[i + 1] + 1;
		}

		// Insert all the indexes that satisfy the condition
		for (int i = time; i < n - time; i++) {
			if (prefLeft[i] >= time && prefRight[i] >= time)
				ans.add(i);
		}
		return ans;
	}
}

