		if time == 0:
			return [i for i in range(len(security))]
		n = len(security)
		left = [0 for i in range(n)]
		right = [0 for i in range(n)]
		for i in range(n-2, -1, -1):
			if security[i] >= security[i+1]:
				left[i] = 1 + left[i+1]
			else:
				left[i] = 0
		for i in range(1, n):
			if security[i] >= security[i-1]:
				right[i] = 1 + right[i-1]
			else:
				right[i] = 0
		ans = []
		for i in range(time, n-time):
			if left[i-time] >= time and right[i+time] >= time:
				ans.append(i)
		return ans

