class Solution {
public:
    vector<int> goodDaysToRobBank(vector<int>& security, int time) {
        // [5,3,3,3,5,6,2]
        // [0,1,2,3,0,0,1] --> count of contiguous no greater than current no to left
        // [0,4,3,2,1,0,0] --> count of contiguous no greater than current no to right
        int n = security.size();
        vector<int> left(n);
        vector<int> right(n);
        left[0] = 0, right[n - 1] = 0;

        for(int i = 1; i < n; i++) {
            if(security[i] <= security[i - 1]) {
                left[i] = left[i - 1] + 1;
            } else {
                left[i] = 0;
            }
        }

        for(int i = n - 2; i >= 0; i--) {
            if(security[i] <= security[i + 1]) {
                right[i] = right[i + 1] + 1;
            } else {
                right[i] = 0;
            }
        }

        vector<int> ans;
        for(int i = 0; i < n; i++) {
            if(left[i] >= time && right[i] >= time) {
                ans.push_back(i);
            }
        }
        return ans;
    }
};
```

