class Solution {
public:
    void forwardUtility(vector<int>& security, vector<int>& forward, int n){
        forward[0] = 0;
        int downcount = 0;
        for (int i = 1; i <= n; i++){
            if (security[i] <= security[i-1]){
                downcount++;
