class Solution {
public:
    vector<int> goodDaysToRobBank(vector<int>& security, int time) {
        int n = security.size();
        int prefix[n], suffix[n];
        prefix[0]=1;
        suffix[n-1]=1;

        for(int i=1; i<n; ++i){
            if(security[i-1]>=security[i]) prefix[i] = 1+prefix[i-1];
            else prefix[i] = 1;
        }

        for(int j=n-2; j>=0; --j){
            if(security[j+1]>=security[j]) suffix[j] = 1+suffix[j+1];
            else suffix[j] = 1;
        }

        vector<int> ans;
        for(int k=0; k<n; ++k){
            if(prefix[k] > time && suffix[k] > time) ans.push_back(k);
        }
        return ans;
    }
};
```
