        vector<int>prefix(n);

        prefix[0]=1;

        for(int i=1;i<n;i++)
        {
            if(security[i]<=security[i-1])
                prefix[i]=prefix[i-1]+1;
            else
                prefix[i]=1;
        }
        vector<int>suffix(n);
         suffix[n-1]=1;
        for(int i=n-2;i>=0;i--)
        {
            if(security[i]<=security[i+1])
                suffix[i]=suffix[i+1]+1;
            else
                suffix[i]=1;
        }
        vector<int>ans;
        for(int i=0;i<n;i++)
        {
            if(prefix[i]>=time+1 && suffix[i]>=time+1)
                ans.push_back(i);
        }
        return ans;
