class Solution {
    public List<Integer> goodDaysToRobBank(int[] security, int time) {
        int [] cntIn = new int[security.length];
        for(int i=1; i<security.length-time; i++){
            if(security[i] > security[i-1]) cntIn[i]=0;
            else cntIn[i] = cntIn[i-1]+1;
        }

        int [] cntDe = new int[security.length];
        for(int i=security.length-2; i>=time; i--){
            if(security[i] > security[i+1]) cntDe[i]=0;
            else cntDe[i] = cntDe[i+1]+1;
