        vector<int>before(security.size());
        vector<int>after(security.size());

        int currsize = 1;
        //filling the before array
        for(int i = 1; i<security.size() ;i++){

            if(security[i]<=security[i-1]){
                before[i] = currsize;
                currsize++;
            }
            else{
                before[i] = 0;
                currsize = 1;
