class Solution {
public:
    // if prev <= cur then dp[cur] = dp[prev] + 1 else 0
    // reverse the arr, run the same, reverse the dp2 => gives increasing
    void getNonIncreasingCntAtIdx(vector<int>&nums, vector<int>&dp)
    {
        dp[0] = 0;
        int prev = nums[0];
        for (int i = 1; i < nums.size();i++)
        {
            dp[i] = prev >= nums[i]
                ? 1 + dp[i-1]
                : 0;
            prev = nums[i];
        }

        return;
    }

    vector<int> goodDaysToRobBank(vector<int>& security, int time) {
        vector<int> dp1(security.size(), 0);
        getNonIncreasingCntAtIdx(security, dp1);

        vector<int> dp2(security.size(), 0);
        reverse(security.begin(), security.end());
        getNonIncreasingCntAtIdx(security, dp2);
        reverse(dp2.begin(), dp2.end());

        vector<int> resArr;
        for (int i = 0; i < dp1.size(); i++)
        {
            if (time <= dp1[i] && time <= dp2[i])
                resArr.push_back(i);
        }

        return resArr;
    }
};
```

