func goodDaysToRobBank(security []int, time int) []int {
	nonInc := make([]int, len(security)+1)
	nonDec := make([]int, len(security)+1)
	for i := 1; i < len(security); i++ {
		if security[i] <= security[i-1] {
			nonInc[i] += nonInc[i-1]+1
		}

		if security[i] >= security[i-1] {
			nonDec[i] += nonDec[i-1]+1
		}
	}

	out := make([]int, 0)
	for i := time; i < len(security)-time; i++ {
		if nonInc[i] >= time && nonDec[i+time] >= time {
			out = append(out, i)
		}
	}
	return out
}

