class Solution:
    def goodDaysToRobBank(self, security: List[int], time: int) -> List[int]:
        if time == 0:
            return [i for i in range(len(security))]

        ni = set()
        nd = set()
        count = 0

        for i in range(1, len(security)):
            if security[i] <= security[i-1]:
                count += 1
            else:
                count = 0

            if count >= time:
                ni.add(i)

        count = 0

        for i in range(len(security)-2, -1, -1):
            if security[i] <= security[i+1]:
                count += 1
            else:
                count = 0

            if count >= time:
                nd.add(i)

        return list(ni&nd)     
