// a[i - t] >= a[i - t + 1] >= ... >=                 (decreasing)
//        a[i]
//               <= ... <= a[i + t - 1] <= a[i + t]   (increasing)
var goodDaysToRobBank = function (a, t) {
  let an = [];
  for (let i = t; i + t < a.length; i++) {
    let bad = 0
    for (let j = i - t + 1; j <= i; j++)
      if (a[j - 1] < a[j]) {
        bad = 1; // increasing
        break
      }
    for (let j = i + 1; j <= i + t; j++)
      if (a[j - 1] > a[j]) {
        bad = 1; // decreasing
        break
      }
    if (!bad) {
      an.push(i)
    }
  }
  return an;
};

