        for i in range(N-2, -1, -1):
            if security[i]<=security[i+1]:
                cnt+=1
            else:
                cnt = 0
            suffix[i] = cnt

        ansArr = []
        for i in range(N):
            if prefix[i]>=time and suffix[i]>=time:
                ansArr.append(i)

        return ansArr

