class Solution {
public:
    vector<int> goodDaysToRobBank(vector<int>& security, int time) {
        int n = security.size();
        vector<pair<int,int>> dp(n,{0,0});
        for(int i=0; i<n-1; i++) {
              dp[i+1].first = security[i] >= security[i+1] ? 1 + dp[i].first : 0;
        }
        for(int i=n-2; i>=0; i--) {
             dp[i].second = security[i] <= security[i+1] ? 1 + dp[i+1].second : 0; 
        }
        vector<int> ans;
        for(int i=0; i<n; i++) {
              if(min(dp[i].first,dp[i].second)>=time) ans.push_back(i);
        }
        return ans;
    }
};

