class Solution {
public:
    vector<int> goodDaysToRobBank(vector<int>& security, int time) {
        int n = security.size();
        vector<int> decs(n), incs(n);
        for(int i = 1;   i < n;  ++i) decs[i] = (security[i] <= security[i-1] ? decs[i-1] + 1 : 0);
        for(int i = n-2; i >= 0; --i) incs[i] = (security[i] <= security[i+1] ? incs[i+1] + 1 : 0);

        vector<int> ans;
        for(int i = time; i < n - time; ++i) {
            if(decs[i]>= time && incs[i] >= time)
                ans.push_back(i);
        }
        return ans;
    }
};
