class Solution {
public:
    vector<int> goodDaysToRobBank(vector<int>& s, int t) {
        int c = 0, prev = INT_MAX;
        vector<bool> h(s.size());
        for (int i = 0; i < s.size(); ++i){
            c = s[i] <= prev ? ++c : 1;
            prev = s[i];
            h[i] = c > t;
        }
        vector<int> ans;
        for (int i = s.size() - 1, c = 0, prev = INT_MAX; i >= 0; --i) {
            c = abs(s[i] <= prev) ? ++c : 1;
            prev = abs(s[i]);
            if (c > t && h[i]) ans.push_back(i);
        }
        return ans;
    }
};
