class Solution {
public:
    vector<int> goodDaysToRobBank(vector<int>& x, int time) {
        int n = int(x.size());
        int dp1[n];
        dp1[0] = 1;
        for(int i = 1 ; i < n ; ++i){
            if(x[i] <= x[i-1]) dp1[i] = 1 + dp1[i-1];
            else dp1[i] = 1;
        }
        int dp2[n];
        dp2[n-1] = 1;
        for(int i = n - 2;  i >= 0 ; --i){
            if(x[i] <= x[i+1]) dp2[i] = 1 + dp2[i+1];
            else dp2[i] = 1;
        }
        vector<int> res;
        for(int i = 0 ; i < n ; ++i){
            if(i - time < 0 ) continue;
            if(i + time > (n-1)) continue;
            int a = dp1[i];
            int b = dp2[i];
            int want = time + 1 ;
            if(a >= want and b >= want) res.push_back(i);
        }
        return res;
    }
};

