        vector<int> ans;
        if(time==0)
        {
            for(int i=0;i<n;i++)
            ans.push_back(i);
            return ans;
        }
        vector<int> left(n,0);
        vector<int> right(n,0);

        left[0]=1;
        int curr=1;
        for(int i=1;i<n;i++)
